	,FROMPERSON
	,TOPERSON
	,POST
	,ADDRESS
	,ADDRESS2
	,PHONE
	,PRODUCTNUMBER
	,PNDNUMBER
	,BUYSTATE			
	)VALUES(
		(SELECT NVL(MAX(BUYNUMBER),0)+1 FROM BUY)
		,#{category}
		,
		${opname}

		,			
		${count}

		,#{price}
		,#{fromPerson}
		,#{toPerson}
		,#{post}
		,#{address}
		,#{address2}
		,#{phone}
		,#{productNumber}
		,#{pndNumber}
		,'결제완료'
	)
