mapper.insert:
INSERT INTO MEMBER(
			memberId
			, password
			, email
			, name
			, post
			, address
			, address2
			, phone
			) VALUES(
			#{memberId}
			, #{password}
			, #{email}
			, #{name}
			, #{post}
			, #{address}
			, #{address2}
			, #{phone}
			)

mapper.update:
UPDATE MEMBER 

	 			 memberId = #{memberId}
	 			 , password = #{password}
	 				 , email = #{email}
	 				 , name = #{name}
	 				 , post = #{post}
	 			 , address = #{address}
	 			 , address2 = #{address2}
	 		 		 , phone = #{phone}
	 		 		 , auth = #{auth}
	 		 	 , startDate = #{startDate}
	 			 , endDate = #{endDate}

WHERE memberId = #{memberId}

mapper.select:
SELECT 
	memberId
FROM UNIONID
WHERE name = #{name}
AND phone = #{phone}

SELECT 
	password
FROM UNIONID
WHERE memberId = #{memberId}
AND phone = #{phone}
