mapper.update:
UPDATE QUESTION


		TITLE = #{title},


		AND CONTENT = #{content},

	AND CALENDAR = SYSDATE
	AND CATEGORY = #{category}

MEMBERID = #{memberId}

mapper.select:
SELECT B.* FROM (SELECT ROWNUM RN ,A.* FROM (

SELECT Q.* FROM QUESTION Q ,MEMBER M
WHERE M.MEMBERID = Q.MEMBERID

)A )B WHERE RN BETWEEN #{start} AND #{end}
